mapper.select:
SELECT transport_id AS transportId, departure_name AS departureName, train_no AS trainNo FROM transport_info WHERE is_visible = TRUE
AND departure_name LIKE CONCAT('%', #{departureName}, '%')
AND arrival_name LIKE CONCAT('%', #{destinationName}, '%')
AND departure_time = #{departureDateTime}
ORDER BY transport_id DESC LIMIT #{limit} OFFSET #{offset}

SELECT I.train_no
FROM TRANSPORT_INFO I
WHERE I.transport_id = (SELECT R.transport_id
                        FROM TRAN_RES R
                        WHERE R.reservation_id = #{reservationId}
                        LIMIT 1);
